package crooy

import zio.{ZIO, ZIOAppDefault, ZLayer}

object Day6 {
  private val testInput1 = Map(
    "mjqjpqmgbljsphdztnvjfqwrcgsmlb"    -> 7,
    "bvwbjplbgvbhsrlpgdmjqwftvncz"      -> 5,
    "nppdvjthqldpwncqszvftbrmjlhg"      -> 6,
    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" -> 10,
    "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"  -> 11
  )

  private val testInput2 = Map(
    "mjqjpqmgbljsphdztnvjfqwrcgsmlb"    -> 19,
    "bvwbjplbgvbhsrlpgdmjqwftvncz"      -> 23,
    "nppdvjthqldpwncqszvftbrmjlhg"      -> 23,
    "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg" -> 29,
    "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"  -> 26
  )
  private val live =
    ""

  def main(args: Array[String]): Unit = {
    println("day 6")
    testInput1.map { case (input, output) =>
      val result = indexOfFirstPacket(4)(input, 0)
      assert(result == output, s"expected $output, got $result for $input")
    }
    println(s"part1 is ${indexOfFirstPacket(4)(live, 0)}")

    testInput2.map { case (input, output) =>
      val result = indexOfFirstPacket(14)(input, 0)
      assert(result == output, s"expected $output, got $result for $input")
    }
    println(s"part2 is ${indexOfFirstPacket(14)(live, 0)}")
  }

  private def indexOfFirstPacket(prefixLength: Int)(input: String, index: Int): Int = {
    if (input.take(prefixLength).distinct.size == prefixLength) index + prefixLength
    else indexOfFirstPacket(prefixLength)(input.drop(1), index + 1)
  }
}
